class Solution {

/**
* @param Integer $n
* @return Integer
*/
function tribonacci($n) {
if ($n &lt;= 0) {
return 0;
}

$retArr = [0,1,1];
for ($i = 3; $i &lt;= $n; $i++) {
$retArr[$i] = $retArr[$i-1] + $retArr[$i-2] + $retArr[$i-3];
}

return $retArr[$n];
}
}

